// contracts/ERC20WithAutoMinerReward.sol
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/access/AccessControl.sol";

contract ERC20WithAutoMinerReward is ERC20, AccessControl {

    event Reward(address indexed _to);

    // Create a new role identifier for the minter role
    bytes32 public constant BURNER_ROLE = keccak256("BURNER_ROLE");

    constructor(uint256 initialSupply) ERC20("Reward", "RWD") {
        _mint(msg.sender, initialSupply);
        _setupRole(BURNER_ROLE, msg.sender);
    }

    //block.coinbase not available in ganache
    function _mintMinerReward() internal {
        if(block.coinbase != address(0)) {
            _mint(block.coinbase, 10);
            emit Reward(block.coinbase);
        }
    }

    function _beforeTokenTransfer(address from, address to, uint256 value) internal virtual override {
        // if (!(from == address(0) && to == block.coinbase)) {
        _mintMinerReward();
        // }
        super._beforeTokenTransfer(from, to, value);
    }

    function burn(uint256 value) public {
        require(hasRole(BURNER_ROLE, msg.sender), "Caller is not a burner");
        _burn(msg.sender, value);
    }

    // function getCoinbase() public returns (address){
    //     return block.coinbase;
    // }

    // REJECT any incoming ether
	fallback() external payable { revert(); }
}
